document.addEventListener("DOMContentLoaded", function(event) {
  var $promoBanner = $('.top-banner-wrapper .top-promo-trigger');
  if($promoBanner.length > -1 && $promoBanner.find("div[data-enableAutoDistrupt]").length > -1){
    //pull this from localStorage or a cookie
    var hasBeenAutoDisrupted = sessionStorage.getItem("gapAutoDisrupted") == "true";
    if(!hasBeenAutoDisrupted && onAutoDisrupPage()){
      setTimeout(function() { $promoBanner.click() } , 1000); //delay trigger for the script to fully load
      setTimeout(function() { $promoBanner.click() } , 7000); //configurable duration from content asset
      //set the localStorage or cookie
      sessionStorage.setItem("gapAutoDisrupted","true");
    }
  }
  function onAutoDisrupPage(){
    var allowedNS = ["search","product","storefront","content"];
    if(allowedNS.indexOf(window.pageContext.ns) > -1){
      return true;
    }
    return false;
  }
});

