// Force Omniture tracking to come from footer CID regarless of what page of site user is on
var forceOmniTracking = function( url, linkNum, localAnchor ){
	var footerCIDNum = "gap-footer";
	var mlink = (url.indexOf('?') != -1) && (url.substring(url.indexOf('?')).length > 2) ? "&mlink=gap-footer," : "?mlink=gap-footer,";
	var newUrl = url + mlink + footerCIDNum + "," + linkNum + "&clink=" + footerCIDNum;
	if( localAnchor ) newUrl = newUrl + '#' + localAnchor;
	top.location.href = newUrl;
	return false;
}


	function ShowHide(arg, arg1) {

		var getDiv = document.getElementById(arg);

		var getSpan = document.getElementById(arg1);



		if (getDiv.style.display == "block") {

			getDiv.style.display = "none";

			getSpan.innerHTML = "+";

		} else {

			getDiv.style.display = "block";

			getSpan.innerHTML = "-";

		}

	}

document.addEventListener("DOMContentLoaded", function() {
        var wcdJlCid;
        if (window.location.href.indexOf('scrollTo') > -1) {
            var getQueryString = function(field, url) {
                var href = url ? url : window.location.href;
                var reg = new RegExp('[?&]' + field + '=([^&#]*)', 'i');
                var string = reg.exec(href);
                return string ? string[1] : null;
            };
             wcdJlCid = getQueryString('scrollTo');
        };
if (wcdJlCid != null || wcdJlCid != undefined ) {
         jQuery('html, body').animate({
            scrollTop: jQuery('.'+ wcdJlCid ).offset().top
        }, 1000);
    }
    });
